        private void load_data_2_grid()
        {
            m_ds = new DS_V_PHIEU_THU();
            m_ds.Clear();
            m_ds.EnforceConstraints = false;
            m_us.FillDataset(m_ds
                               , m_dat_tu_ngay.Value.Date
                               , m_dat_den_ngay.Value.Date
                               , CIPConvert.ToDecimal(m_cbo_nhan_vien.SelectedValue));
            m_fg.Redraw = false;
            CGridUtils.Dataset2C1Grid(m_ds, m_fg, m_obj_trans);
            CGridUtils.MakeSoTT(0, m_fg);
            m_fg.Redraw = true;
            create_tree_2grid();
            wrap_text_cell();
        }
        private void grid2us_object(US_V_PHIEU_THU i_us
            , int i_grid_row)
