                m_us_dm_hoc_sinh.strDIA_CHI = m_txt_dia_chi.Text;
            }
            if (m_txt_ho_ten_phu_huynh.Text == "")
            {
                m_us_dm_hoc_sinh.SetHO_TEN_PHNull();
            }
            else
            {
                m_us_dm_hoc_sinh.strHO_TEN_PH = m_txt_ho_ten_phu_huynh.Text;
            }
            if (m_txt_email_phu_huynh.Text == "")
